const linkMap=document.querySelector(".link-open-map"),popupMap=document.querySelector(".modal-map"),closeMap=popupMap.querySelector(".modal-close");linkMap.addEventListener("click",function(e){e.preventDefault(),popupMap.classList.add("modal--show")}),closeMap.addEventListener("click",function(e){e.preventDefault(),popupMap.classList.remove("modal--show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&popupMap.classList.contains("modal--show")&&(e.preventDefault(),popupMap.classList.remove("modal--show"))});